import React, {
  Component,
  Fragment
} from 'react';

import cardback from './img/back.png';
import cardfront from './img/front.jpg';

class Card extends Component {
  render() {
    return(
      <div className={ `part-cards-card${ (!this.props.isCasted) ? "" : " casted"  }` }>
        <div className="part-cards-card-front">
          <img
              src={ cardback }
              className="part-cards-card-front-image"
              alt="card"
          />          
        </div>
        <div className="part-cards-card-back">
          <img
            src={ cardfront }
            className="part-cards-card-back-image"
            alt="card"
          />
          <span className="part-cards-card-back-mark">15</span>
        </div>
      </div>
    );
  }
}

class Cards extends Component {
  render() {
    return(
      <div className={ `part-cards ${ this.props.type.toLowerCase() } ${ this.props.target.toLowerCase() }` }>
        <Card />
        <Card />
        <Card />
        <Card />
      </div>
    );
  }
}

class Part extends Component {
  render() {
    return(
      <div className={ `part ${ this.props.type.toLowerCase() }` }>
        <Cards
          type={ this.props.type }
          target={ this.props.target }
        />
      </div>
    );
  }
}

class Desk extends Component {
  render() {
    return(
      <div className="desk">
        <div />
        <div className="desk-flow">
          <Card
            isCasted={ true }
          />
        </div>
        <div className="desk-cards">
          <img src={ cardback } alt="desk cards" className="desk-cards-image" />
        </div>
      </div>
    );
  }
}

class App extends Component {
  render() {
    return(
      <Fragment>
        <Part
          type="top"
          target="bot"
        />
        <Desk />
        <Part
          type="bottom"
          target="player"
        />
      </Fragment>
    );
  }
}

export default App;
