import React, {
  Component,
  Fragment
} from 'react';
import { connect } from 'react-redux';

import Desk from './components/Desk';
import Card from './components/Card';
import Initmodal from './components/Initmodal';

import { cardback, cardfront } from './images';

class Cards extends Component {
  render() {
    return(
      <div className={ `part-cards ${ this.props.type.toLowerCase() } ${ this.props.target.toLowerCase() }` }>
        {/* <Card /> */}
      </div>
    );
  }
}

class Part extends Component {
  render() {
    return(
      <div className={ `part ${ this.props.type.toLowerCase() }` }>
        {/* <Cards
          type={ this.props.type }
          target={ this.props.target }
        /> */}
      </div>
    );
  }
}

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      initModal: false
    }
  }

  componentDidMount() {
    this.setState(() => ({ initModal: true }));
  }

  render() {
    return(
      <Fragment>
        <Initmodal />
        <Part
          type="top"
          target="bot"
        />
        <Desk />
        <Part
          type="bottom"
          target="player"
        />
      </Fragment>
    );
  }
}

const mapStateToProps = ({ global }) => ({
  global
});

const mapActionsToProps = {
  startGame: () => ({ type: 'INITIALIZE_GAME', payload: true })
}

export default connect(
  mapStateToProps,
  mapActionsToProps
)(App);
