import rules from '../rules';

function reducer(state = {}, { type, payload }) {
    let a = Object.assign({}, state);

    switch(type) {
        case 'INITIALIZE_GAME':
            a.deskSize = rules.deskSize;
            a.botDesk = a.playerDesk = [];
            a.inGame = true;
        break;
        default:break;
    }

    return a;
}

export default reducer;
