import rules from '../rules';

function reducer(state = {}, { type, payload }) {
    let a = Object.assign({}, state);

    switch(type) {
        case 'INITIALIZE_GAME': // WARNING: Store enter point
            a = {
                deskSize: rules.deskSize,
                botDesk: [],
                playerDesk: [],
                inGame: true
            }
        break;
        default:break;
    }

    return a;
}

export default reducer;
