import React, {
  Component,
  Fragment
} from 'react';

import cardback from './img/back.png';
import cardfront from './img/front.jpg';

class Card extends Component {
  render() {
    return(
      <div className="part-cards-card">
        <div className="part-cards-card-front">
          <img
              src={ cardback }
              className="part-cards-card-front-image"
              alt="card"
          />          
        </div>
        <div className="part-cards-card-back">
          <img
            src={ cardfront }
            className="part-cards-card-back-image"
            alt="card"
          />
          <span className="part-cards-card-back-mark">15</span>
        </div>
      </div>
    );
  }
}

class Cards extends Component {
  render() {
    return(
      <div className={ `part-cards ${ (this.props.type.toUpperCase() === "TOP") ? "top" : "bottom" }` }>
        <Card />
      </div>
    );
  }
}

class Part extends Component {
  render() {
    return(
      <div className="part">
        <Cards
          type={ this.props.type }
        />
      </div>
    );
  }
}

class Desk extends Component {
  render() {
    return(
      <div className="desk">

      </div>
    );
  }
}

class App extends Component {
  render() {
    return(
      <Fragment>
        <Part
          type="top"
        />
        <Desk />
        <Part
          type="bottom"
        />
      </Fragment>
    );
  }
}

export default App;
